#AMT Shader replacer - amt_sr
#Purpose: To replace all old 64x64 shaders with 512x8 UHD shaders


import struct
import math

print("ONLY WORKS WITH 64x64 SHADERS, NOT 64x8 SHADERS USED IN NEWER IW MODELS")

def main():
    print("AMT name (with extension):")
    #opens AMT
    x = input("")
    x = x.replace("\"", "")
    f = open(x, "r+b")
    chunk = f.read(16)
    offsets = []
    counter = 0
    
    #replace info in the 3 line info blocks
    while chunk != b"":
        if chunk[4] == 0x01 and chunk[8] == 0x13:
            offsets.append(((f.tell()-16)))
            print(hex(f.tell()-16))
            counter += 1
        chunk = f.read(16)
    for i in offsets:
        f.seek(i+12)
